import React, { useState } from 'react';
import './login.css'
import DropdownSelector from './drop-box-login'; 
import HomePage from './monitor-screen';

import { useNavigate } from "react-router-dom"; // Import useHistory

const patients = [
    {
        name: 'Sara Norman',
        id: '5344-9709',
        doctor: 'Dr. Jason Rosenberg',
        doctorPhone: '579-0432',
        glucoseLevels: { low: 80, normal: [80, 140], high: 140 }
      },
      {
        name: 'Gregg Norman',
        id: '1275-4307',
        doctor: 'Dr. Nikhil Singh',
        doctorPhone: '334-2309',
        glucoseLevels: { low: 70, normal: [70, 120], high: 120 }
      }
    ];

function Login() {
    const navigate = useNavigate(); // Use the useNavigate hook
    const [selectedPatientId, setSelectedPatientId] = useState('');

    // Handle the selection change
    const handleSelectionChange = (id) => {
        setSelectedPatientId(id);
    };

    // Handle the login button click
    const handleLoginClick = () => {
        // Assuming the DropdownSelector calls handleSelectionChange when a patient is selected
        const selectedPatient = patients.find(patient => patient.id.toString() === selectedPatientId);
        if (selectedPatient) {
            // Save the selected patient's details for use in the next screen
            localStorage.setItem('selectedPatient', JSON.stringify(selectedPatient));
            // Navigate to the monitor screen
            navigate('/monitor-screen');
        }
    };
    return (
    <div className = "loginpage"> 
        <div className = "login-box"> 
            <p className = "welc-txt">Welcome</p>
            <p className = "login-to-cont">Login to continue</p>
            <DropdownSelector patients={patients}  onSelectionChange={handleSelectionChange}/>
            <button className = "login-btn" onClick = {handleLoginClick}> Login </button> 
        </div> 
    </div>
    );
}

export default Login;
