import React, { useState } from 'react';



  function DropdownSelector({ patients }) {
    const [selectedValue, setSelectedValue] = useState('');

    const handleChange = (event) => {
      setSelectedValue(event.target.value);
    };
      // Example inline CSS styles
    const divStyle = {
      textAlign: 'center', // Center align the dropdown
      marginTop: '20px', // Add some space at the top
    };

    const selectStyle = {
      width: '250px', // Set a specific width for the dropdown
      padding: '10px', // Add some padding inside the dropdown
      backgroundColor: '#f1f1f1', // Light grey background
      border: '1px solid #ddd', // Light grey border
      borderRadius: '5px', // Rounded corners
      textAlign: 'center',
      justifyCenter: 'center',
      left: '200px',
    };

    return (
      <div>
        <select value={selectedValue} onChange={handleChange} style={selectStyle}>
          <option value="">Select a patient</option>
          {patients.map((patient) => (
            <option key={patient.id} value={patient.id}>{patient.name}</option>
          ))}
        </select>
      </div>
    );
  }

  export default DropdownSelector;
