import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';
import './monitor.css'
import profLogo from './account.png'
import { useNavigate } from 'react-router-dom';
import DropdownSelector from './drop-box-login'; 

function HomePage(){ 
    const location = useLocation();
    const user = location.state.user; 
    const [glucoseLevel, setGlucoseLevel] = useState('');
    const [reason, setReason] = useState('');
    const navigate = useNavigate();

    const handleGlucoseChange = (e) => {
        setGlucoseLevel(e.target.value);
    };

    const handleReasonChange = (e) => {
        setReason(e.target.value);
    };


    //for user input vals ... 
    const [inputValue, setInputValue] = useState(""); 
    const handleChange = (event) => {
      setInputValue(event.target.value); 
    };
    const handleSubmit = (event) => {
      event.preventDefault(); // Prevent the default form submit action
      console.log("Submitted value:", inputValue); // Log or handle the submitted value
      // Add actions to use the inputValue, like sending to an API or processing it
    };


    const [currentQuestion, setCurrentQuestion] = useState(1);
    const [answers, setAnswers] = useState([]);

    const handleYes = () => {
        const newAnswers = [...answers, 'yes']; 
        setAnswers(newAnswers); // Update 
        setCurrentQuestion(currentQuestion + 1);
    };

    const handleNo = () => {
        const newAnswers = [...answers, 'no']; 
        setAnswers(newAnswers); // Update 
        setCurrentQuestion(currentQuestion + 1);
    };

    const handleLogout = () => {
        navigate('/login-page');
    };

    const handleGSubmit = () => {
        let message = "Your glucose level is normal.";
        setCurrentQuestion(currentQuestion + 1);
        if (glucoseLevel < user.glucoseLevels.low) {
            message = "Your glucose level is low.";
            setCurrentQuestion(currentQuestion + 1);
        } else if (glucoseLevel > user.glucoseLevels.high) {
            message = "Your glucose level is high.";
            setCurrentQuestion(currentQuestion + 1);
        }
    };

    const handleLastSubmit = () => { 
        setCurrentQuestion(5);
    }


    const renderQuestion = () => {
        if (currentQuestion === 1) {
            return (
                <div className="littleqbox">
                <p className="question">Have you taken your blood sugar reading today?</p>
                <button className="yes-btn" onClick={handleYes}>Yes</button>
                <button className="no-btn" onClick={handleNo}>No</button>
                </div>
            );
        } else if (currentQuestion === 2 && answers[0] === 'no') {
            return (
                <div className="littleqbox">
                <p className="question">Can you please take your blood sugar reading now, and enter it below?</p>
                <form onSubmit={handleSubmit}>
                        <label htmlFor="textInput">Enter Blood Sugar Level(0-999):</label>
                        <input
                            type="text"
                            id="textInput"
                            value={inputValue}
                            onChange={handleChange}
                        />
                        <button onClick={handleLastSubmit}>Submit</button>
                </form>
                </div>
            );
        } else if (currentQuestion === 2 && answers[0] === 'yes') {
            return (
                <div className="littleqbox">
                    <p className="question">Please enter your reading below.</p>
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="textInput">Enter Blood Sugar Level(0-999):</label>
                        <input
                            type="text"
                            id="textInput"
                            value={inputValue}
                            onChange={handleChange}
                        />
                        <button onClick={handleGSubmit}>Submit</button>
                    </form>
                </div>
            );
        } else if(currentQuestion === 3 && (inputValue > 200 || inputValue < 30)) { 
            return (
                <div className="littleqbox">
                    <p className="question">This reading is abnormal. Why do you feel like your reading isn't normal?</p>
                    <textarea
                        id="reasonInput"
                        value={reason}
                        onChange={handleReasonChange}
                        rows="4" // Number of rows to define the height
                        cols="50" // Number of cols to define the width, or use CSS
                    />
                    <button onClick={handleGSubmit}>Submit</button>
                </div>
            );
        }else if(currentQuestion === 3 && inputValue < user.glucoseLevels.low){ 
            return (
                <div className="littleqbox">
                    <p className="question">This is a low reading. Eat sugar, meals, and snacks as described by {user.doctorName}.</p>
                </div>
            );
        }else if(currentQuestion === 3 && inputValue > user.glucoseLevels.high){ 
            return (
                <div className="littleqbox">
                    <p className="question">This is a high reading. Call {user.doctorName} immediately. Phone Number: {user.doctorPhone}.</p>
                </div>
            );
        }else if(currentQuestion === 3){ 
            return (
                <div className="littleqbox">
                    <p className="question"> Your reading is in a normal range.</p>
                </div>
            );
        } else if(currentQuestion === 4){ 
            return (
                <div className="littleqbox">
                    <p className="question"> Your reading is in a normal range.</p>
                </div>
            );
        } else if(currentQuestion === 5){ 
            return (
                <div className="littleqbox">
                    <p className="question"> Please log out by pressing the log-out button below.</p>
                    <button className="logout" onClick={handleLogout}>Log out</button>
                </div>
            );
        }
    };
    return (
        <div>
            <header> 
                <nav className = "navBar"> 
                    <div className = "nav-cont"> 
                        <div className = "nav-elements"> 
                            <ul> 
                                <li> 
                                    <img className ="profileIcon"style={{width: 70, height: 70}}src = {profLogo}/> 
                                </li> 
                                <li> 
                                    <div className = "Name">{user.name}</div> 
                                    <div className = "ID">ID: {user.id}</div>
                                </li>
                                <li> 
                                    <button className = "logout">Log out</button>
                                </li> 
                            </ul> 
                        </div>  
                    </div>
                </nav>
            </header>
            <div className = "monitorpage"> 
                <div className = "q-container">
                    <p className = "title">Blood Glucose Monitoring</p>
                    <p className = "lil-title">Please answer the following question(s).</p>
                    {renderQuestion()}
                </div>
            </div> 
        </div>

    );
}
export default HomePage;
