import React, { useState } from 'react';
import './monitor.css'
import profLogo from './account.png'
import DropdownSelector from './drop-box-login'; 

import ReactDOM from 'react-dom/client';
import reportWebVitals from './reportWebVitals';

function HomePage(){ 
    const selectedPatient = JSON.parse(localStorage.getItem('selectedPatient'));
        // State to track the current question number
    const [currentQuestion, setCurrentQuestion] = useState(1);
    // State to track answers, initially empty
    const [answers, setAnswers] = useState([]);

    // Function to handle "Yes" button click
    const handleYes = () => {
        const newAnswers = [...answers, 'yes']; // Add 'yes' to the answers array
        setAnswers(newAnswers); // Update the answers state
        // Logic to move to the next question or perform other actions
        setCurrentQuestion(currentQuestion + 1);
    };

    // Function to handle "No" button click
    const handleNo = () => {
        const newAnswers = [...answers, 'no']; // Add 'no' to the answers array
        setAnswers(newAnswers); // Update the answers state
        // Logic to move to the next question or perform other actions
        setCurrentQuestion(currentQuestion + 1);
    };

    // Example of conditional rendering based on the current question and answers
    const renderQuestion = () => {
        if (currentQuestion === 1) {
        return (
            <div className="littleqbox">
            <p className="question">Have you taken your blood sugar reading today?</p>
            <button className="yes-btn" onClick={handleYes}>Yes</button>
            <button className="no-btn" onClick={handleNo}>No</button>
            </div>
        );
        } else if (currentQuestion === 2 && answers[0] === 'no') {
        return (
            <div className="littleqbox">
            <p className="question">Would you like to take it now?</p>
            <button className="yes-btn" onClick={handleYes}>Yes</button>
            <button className="no-btn" onClick={handleNo}>No</button>
            </div>
        );
        } else if (currentQuestion === 2 && answers[0] === 'yes') {
        return (
            <div className="littleqbox">
            <p className="question">Great! Please enter your reading.</p>
            {/* Input for reading */}
            </div>
        );
        }
        // Continue adding else-if blocks for further questions
  };
    return (
        <div>
            <header> 
                <nav className = "navBar"> 
                    <div className = "nav-cont"> 
                        <div className = "nav-elements"> 
                            <ul> 
                                <li> 
                                    <img className ="profileIcon"style={{width: 70, height: 70}}src = {profLogo}/> 
                                </li> 
                                <li> 
                                    <div className = "Name">Sara Norman</div> 
                                    <div className = "ID">ID: 53449709</div>
                                </li>
                                <li> 
                                    <button className = "logout">Log out</button>
                                </li> 
                            </ul> 
                        </div> 
                    </div>
                </nav>
            </header>
            <div className = "monitorpage"> 
                <div className = "q-container">
                    <p className = "title">Blood Glucose Monitoring</p>
                    <p className = "lil-title">Please answer the following question(s).</p>
                    {renderQuestion()}
                </div>
            </div> 
        </div>

    );
}
export default HomePage;
