import React, { useState } from 'react';
import './login.css'
import DropdownSelector from './drop-box-login'; 
import HomePage from './monitor-screen';

import { useNavigate } from "react-router-dom"; // Import useHistory
const usersData = [
    {
      name: 'Sara Norman',
      id: '5344-9709',
      doctorName: 'Dr. Jason Rosenberg',
      doctorPhone: '579-0432',
      glucoseLevels: {
        low: 80,
        high: 140
      }
    },
    {
      name: 'Gregg Norman',
      id: '1275-4307',
      doctorName: 'Dr. Nikhil Singh',
      doctorPhone: '334-2309',
      glucoseLevels: {
        low: 70,
        high: 120
      }
    }
  ];


function Login({onLogin}) {
    const navigate = useNavigate(); // Use the useNavigate hook
    const [selectedUserId, setSelectedUserId] = useState('');

    // Dropdown selector handler
    const handleSelectionChange = (event) => {
      setSelectedUserId(event.target.value);
    };

    // Login button click handler
    const handleLoginClick = () => {
      const user = usersData.find(user => user.id === selectedUserId);
      if (user) {
        // Perform login logic and navigate to HomePage
        // The user information can be passed via state in navigate (using React Router v6)
        navigate('/home', { state: { user } });
      } else {
        // Handle case where user does not select a valid option or none is selected
        alert('Please select a user to login.');
      }
    };
    return (
    <div className = "loginpage"> 
        <div className = "login-box"> 
            <p className = "welc-txt">Welcome</p>
            <p className = "login-to-cont">Login to continue</p>
            <DropdownSelector patients={usersData} />
            <button className = "login-btn"> Login </button> 
        </div> 
    </div>
    );
}

export default Login;
