import './App.css';
import React from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Login from './login-page';
import HomePage from './monitor-screen'; // Assuming you have a HomePage component

const root = ReactDOM.createRoot(document.getElementById('root'));
const users = [
  {
    name: 'Sara Norman',
    id: '5344-9709',
    doctor: 'Dr. Jason Rosenberg',
    doctorPhone: '579-0432',
    glucoseLevels: { low: 80, normal: [80, 140], high: 140 }
  },
  {
    name: 'Gregg Norman',
    id: '1275-4307',
    doctor: 'Dr. Nikhil Singh',
    doctorPhone: '334-2309',
    glucoseLevels: { low: 70, normal: [70, 120], high: 120 }
  }
];
function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const handleLogin = (userName) => {
    const user = usersData.find(user => user.name.split(" ")[0] === userName);
    setCurrentUser(user);
  };

  const handleLogout = () => {
    setCurrentUser(null);
  };

  return (
    <Router>
      <div className="App">
        {/* Removed header section with "My App" link */}
        {!currentUser ? (
        <Login onLogin={handleLogin} />
      ) : (
        <MonitorScreen user={currentUser} onLogout={handleLogout} />
      )}
        <Routes>
          <Route path="/" element={<Login/>} />
          <Route path = "/monitor-screen" element = {<HomePage/>}/>
          {/* Add other routes here */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
