import React, { useState } from 'react';

function DropdownSelector() {
  const [selectedValue, setSelectedValue] = useState(''); // Initial state is empty or your default value

  // Handler for when an option is selected
  const handleChange = (event) => {
    setSelectedValue(event.target.value);
    // You can also do other things with the selected value here
  };

  return (
    <div>
      <select value={selectedValue} onChange={handleChange}>
        <option value="">Select an option</option> {/* Optional: Placeholder option */}
        <option value="Sara Norman">Option 1</option>
        <option value="Gregg Norman">Option 2</option>
        {/* Add more options as needed */}
      </select>
    </div>
  );
}

export default DropdownSelector;
